const emails = $input.all();
const extractedData = [];

for (let i = 0; i < emails.length; i++) {
  const email = emails[i].json;

  let body = email.text || email.snippet || '';

  if (!body && email.textAsHtml) {
    body = email.textAsHtml;
  }

  // Clean HTML
  if (body.includes('<') && body.includes('>')) {
    body = body.replace(/<[^>]*>/g, ' ')
      .replace(/&nbsp;/g, ' ')
      .replace(/&amp;/g, '&')
      .replace(/&lt;/g, '<')
      .replace(/&gt;/g, '>')
      .replace(/\s+/g, ' ')
      .trim();
  }

  // Remove quoted replies
  body = body.split('\n')
    .filter(line => !line.trim().startsWith('>'))
    .join('\n');

  body = body.replace(/On .+ wrote:/gi, '');
  body = body.replace(/reacted via Gmail/gi, '');
  body = body.replace(/---------- Forwarded message ---------/gi, '');

  // Remove signatures
  const signatureMarkers = [
    /\n--\s*\n/,
    /\n_{3,}\n/,
    /\nSent from /i,
    /\nGet Outlook for /i,
    /\nBest regards/i,
    /\nThanks,/i,
    /\nCheers/i
  ];

  for (const marker of signatureMarkers) {
    const parts = body.split(marker);
    if (parts.length > 1) {
      body = parts[0].trim();
      break;
    }
  }

  body = body.replace(/\n{3,}/g, '\n\n')
    .replace(/[ \t]+/g, ' ')
    .trim();

  // Skip if empty after cleaning
  if (body.length === 0) {
    continue;
  }

  const subject = email.subject || '(No subject)';
  const date = email.date || email.receivedDateTime || '';
  const snippet = body.substring(0, 150) + (body.length > 150 ? '...' : '');

  // ðŸ”¥ IMPROVED: Extract sender info (works for any Gmail account)
  let fromAddress = '';
  let fromName = '';

  if (email.from && email.from.value && email.from.value[0]) {
    // Gmail API format
    fromAddress = email.from.value[0].address || '';
    fromName = email.from.value[0].name || '';
  } else if (email.from && typeof email.from === 'string') {
    // Simple string format
    fromAddress = email.from;
    // Try to extract name if format is "Name <email@example.com>"
    if (fromAddress.includes('<')) {
      const match = fromAddress.match(/^(.+?)\s*<(.+?)>$/);
      if (match) {
        fromName = match[1].trim();
        fromAddress = match[2].trim();
      }
    }
  }

  // If no name extracted, use email prefix as name
  if (!fromName && fromAddress) {
    fromName = fromAddress.split('@')[0];
  }

  extractedData.push({
    emailNumber: i + 1,
    emailId: email.id || email.messageId || `email_${i}`,
    subject: subject,
    body: body,
    snippet: snippet,
    date: date,
    from: fromAddress,
    fromName: fromName
  });
}

// Return in n8n format
return extractedData.map(item => ({ json: item }));

// Get all emails from previous node
const emails = $input.all();
const cleanedEmails = [];

for (const item of emails) {
  // Properly access the json object
  const email = item.json;

  // Check if email object exists and has body
  if (!email || !email.body || typeof email.body !== 'string' || email.body.trim().length === 0) {
    // Log skipped email for debugging
    cleanedEmails.push({
      json: {  //  Wrap in json object
        emailId: email?.emailId || 'unknown',
        subject: email?.subject || 'No subject',
        error: 'Empty or invalid body',
        skipped: true,
        originalBodyLength: email?.body ? email.body.length : 0
      }
    });
    continue;
  }

  // Clean the body text (preserve content, just remove noise)
  let cleanBody = email.body
    // Remove common email artifacts
    .replace(/reacted via Gmail/gi, '')
    .replace(/\[cid:.*?\]/g, '') // Remove image references
    .replace(/\r\n/g, '\n') // Normalize line breaks
    // Clean up excessive whitespace
    .replace(/\n{3,}/g, '\n\n') // Max 2 line breaks
    .replace(/[ \t]+/g, ' ') // Single spaces
    .trim();

  // Split on common signature markers (but keep everything before)
  const signatureMarkers = [
    /\n--\s*\n/,
    /\n_{3,}\n/,
    /\nSent from /i,
    /\nGet Outlook for /i,
    /\nBest regards/i,
    /\nThanks/i
  ];

  for (const marker of signatureMarkers) {
    const parts = cleanBody.split(marker);
    if (parts.length > 1) {
      cleanBody = parts[0].trim();
    }
  }

  // Basic measurements
  const wordArray = cleanBody.split(/\s+/).filter(w => w.length > 0);
  const wordCount = wordArray.length;

  // Better empty handling
  if (wordCount === 0) {
    cleanedEmails.push({
      json: {  // Wrap in json object
        emailId: email.emailId,
        subject: email.subject,
        error: 'Empty after cleaning',
        skipped: true,
        originalBody: email.body.substring(0, 100)
      }
    });
    continue;
  }

  // Character metrics
  const totalChars = cleanBody.length;
  const totalCharsNoSpaces = cleanBody.replace(/\s/g, '').length;
  const avgWordLength = wordCount > 0 ? totalCharsNoSpaces / wordCount : 0;

  // Sentence analysis
  const sentences = cleanBody.match(/[.!?]+(?=\s|$)/g) || [];
  const sentenceCount = sentences.length || 1;
  const avgSentenceLength = wordCount / sentenceCount;

  // Punctuation patterns
  const exclamationCount = (cleanBody.match(/!/g) || []).length;
  const questionCount = (cleanBody.match(/\?/g) || []).length;
  const periodCount = (cleanBody.match(/\./g) || []).length;
  const commaCount = (cleanBody.match(/,/g) || []).length;

  // Emoji detection (all emoji ranges)
  const emojiMatches = cleanBody.match(/[\u{1F300}-\u{1F9FF}]|[\u{2600}-\u{26FF}]|[\u{2700}-\u{27BF}]/gu) || [];
  const emojiCount = emojiMatches.length;

  // Greeting patterns (multiple languages)
  const greetingPatterns = /^(hi|hello|hey|dear|greetings|good morning|good afternoon|good evening|bonjour|salut|hola|ciao)/i;
  const startsWithGreeting = greetingPatterns.test(cleanBody);

  // Closing patterns (multiple languages)
  const closingPatterns = /(best regards|sincerely|regards|cheers|thanks|thank you|yours|cordially|respectfully|faithfully|cordialement|salutations)/i;
  const hasFormalClosing = closingPatterns.test(cleanBody);

  // Casual language indicators
  const contractionsPattern = /(i'm|you're|we're|they're|it's|don't|can't|won't|shouldn't|wouldn't|couldn't|isn't|aren't|wasn't|weren't|haven't|hasn't|hadn't)/i;
  const usesContractions = contractionsPattern.test(cleanBody);

  // Uppercase analysis (shouting detection)
  const uppercaseWords = cleanBody.match(/\b[A-Z]{2,}\b/g) || [];
  const uppercaseRatio = uppercaseWords.length / (wordCount || 1);

  // Number detection
  const numberCount = (cleanBody.match(/\b\d+\b/g) || []).length;

  // URL detection
  const urlCount = (cleanBody.match(/https?:\/\/[^\s]+/g) || []).length;

  // Calculate formality score (0-10)
  let formalityScore = 5; // neutral baseline

  if (hasFormalClosing) formalityScore += 2;
  if (avgWordLength > 6) formalityScore += 1;
  if (avgWordLength > 7) formalityScore += 1;
  if (avgSentenceLength > 20) formalityScore += 1;

  if (usesContractions) formalityScore -= 1;
  if (emojiCount > 0) formalityScore -= 2;
  if (exclamationCount > 2) formalityScore -= 1;
  if (uppercaseRatio > 0.1) formalityScore -= 1;

  formalityScore = Math.max(0, Math.min(10, formalityScore));

  // Determine content type
  let contentType = 'conversational';
  if (numberCount > wordCount * 0.3) contentType = 'data-heavy';
  else if (urlCount > 2) contentType = 'informational';
  else if (formalityScore >= 7) contentType = 'formal';
  else if (formalityScore <= 3) contentType = 'casual';


// Determine tone
let tone = 'neutral';
if (exclamationCount > 2 && emojiCount > 0) tone = 'enthusiastic';
else if (questionCount > exclamationCount * 2) tone = 'inquisitive';
else if (formalityScore >= 7) tone = 'professional';
else if (formalityScore <= 3 && usesContractions) tone = 'friendly';
  // Return items with proper n8n structure
  cleanedEmails.push({
    json: {
      // Identity
      emailId: email.emailId,
      subject: email.subject,
      date: email.date,
      from: email.from || email.fromAddress,
      to: email.to || email.toAddress,

      // Content
      cleanBody: cleanBody,
      preview: cleanBody.substring(0, 150) + (cleanBody.length > 150 ? '...' : ''),

      // Basic metrics
      wordCount: wordCount,
      charCount: totalChars,
      charCountNoSpaces: totalCharsNoSpaces,

      // Word analysis
      avgWordLength: Math.round(avgWordLength * 10) / 10,

      // Sentence analysis
      sentenceCount: sentenceCount,
      avgSentenceLength: Math.round(avgSentenceLength * 10) / 10,

      // Punctuation
      exclamationCount: exclamationCount,
      questionCount: questionCount,
      periodCount: periodCount,
      commaCount: commaCount,

      // Style indicators
      emojiCount: emojiCount,
      hasEmojis: emojiCount > 0,
      startsWithGreeting: startsWithGreeting,
      hasFormalClosing: hasFormalClosing,
      usesContractions: usesContractions,
      uppercaseRatio: Math.round(uppercaseRatio * 100) / 100,

      // Content features
      numberCount: numberCount,
      urlCount: urlCount,

      // Calculated scores
      formalityScore: formalityScore,
      contentType: contentType,

      // Debugging
      skipped: false
    }
  });
}

return cleanedEmails;
